import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import BasicTable from './Table'
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import ContactList from './ContactList';
const CONTACTS_REST_API_URL = 'http://localhost:8080/api/';




class App extends Component {
  state = {
    isLoading: true,
    contacts: []
  };

  async componentDidMount() {
    axios.get(CONTACTS_REST_API_URL + 'contacts')
      .then(response => {
        console.log(response)
        console.log(response.data)
        this.setState({contacts: response.data, isLoading: false})

      })
      // .then(data => {
      //   console.log(data)
      //   this.setState({contacts: data, isLoading: false})
      //
      // });
  //   const response = await axios.get(CONTACTS_REST_API_URL + 'contacts');
  //   const body = await response.json();
  //   this.setState({ contacts: body, isLoading: false });
   }

  render() {
    const {contacts, isLoading} = this.state;
    console.log(contacts)

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <div className="App-intro">
            <h2>JUG List</h2>
            {contacts.map(group =>
              <div key={group.id}>
                {group.name}
              </div>
            )}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
