import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { deepOrange, deepPurple } from '@material-ui/core/colors';


import axios from 'axios'
import AddContactDialog from './AddContactDialog'
const CONTACTS_REST_API_URL = 'http://localhost:8080/api/';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  orange: {
    color: theme.palette.getContrastText(deepOrange[500]),
    backgroundColor: deepOrange[500],
  },
  purple: {
    color: theme.palette.getContrastText(deepPurple[500]),
    backgroundColor: deepPurple[500],
  },
  table: {
    minWidth: 650,
  },
}));

const backgroundColors =['red', 'orange', 'green', 'blue', 'purple', 'black', 'brown']





export default function ContactTable({rows, openEdit}) {
  const classes = useStyles();

  const openEditDialog = (row)=>{
    openEdit(row)
  }

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell align="right"></TableCell>
            <TableCell align="right">Last Name</TableCell>
            <TableCell align="right">First Name</TableCell>
            <TableCell align="right">Number</TableCell>
            <TableCell align="right">Address</TableCell>
            <TableCell align="right">Email</TableCell>

          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id} onClick={()=>openEditDialog(row)}>
              <TableCell align="right"><Avatar style={{backgroundColor:backgroundColors[Math.floor(Math.random() * backgroundColors.length)]}}>{row.lastName[0] || ''}{row.firstName[1] || ''}</Avatar></TableCell>
              <TableCell align="right">{row.lastName}</TableCell>
              <TableCell align="right">{row.firstName}</TableCell>
              <TableCell align="right">{row.phoneNumber}</TableCell>
              <TableCell align="right">{row.address}</TableCell>
              <TableCell align="right">{row.email}</TableCell>

            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
