import React, { Component } from 'react';

import axios from 'axios'
import ContactTable from './ContactTable'

import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';

const CONTACTS_REST_API_URL = 'http://localhost:8080/api/';


class ContactBook extends Component {
  state = {
    isLoading: true,
    contacts: []
  };

  async componentDidMount() {
    axios.get(CONTACTS_REST_API_URL + 'contacts')
      .then(response => {
        console.log(response)
        console.log(response.data)
        this.setState({contacts: response.data, isLoading: false})

      })

  }

  render() {
    const {contacts, isLoading} = this.state;
    console.log(contacts)

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (

      <div>
        <ContactTable rows={contacts}/>
      </div>

    );
  }
}

export default ContactBook;
