import React, { Component } from 'react';

import axios from 'axios'
import ContactTable from './ContactTable'
import AddContactDialog from './AddContactDialog'

import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';


const CONTACTS_REST_API_URL = 'http://localhost:8080/api/';


class ContactBook extends Component {
  state = {
    isLoading: true,
    contacts: [],
    addContact:false
  };

  async componentDidMount() {
    axios.get(CONTACTS_REST_API_URL + 'contacts')
      .then(response => {
        console.log(response)
        console.log(response.data)
        this.setState({contacts: response.data, isLoading: false})

      })

  }

  render() {
    const {contacts, isLoading} = this.state;
    console.log(contacts)

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (

      <div>
        <div>
          <TextField label="freeSolo"  margin="normal" variant="outlined" />
          <Button variant="contained" color="primary"onClick={()=>this.setState({ addContact:true})} >
            Add Contact
          </Button>
          <AddContactDialog open={this.state.addContact}/>
        </div>
        <ContactTable rows={contacts}/>
      </div>

    );
  }
}

export default ContactBook;
